import React from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  TouchableOpacity,
  Image,
} from "react-native";

import { Ionicons, MaterialCommunityIcons, Feather } from "@expo/vector-icons";
import { statusBarConfig } from "../../Variable";
import BottomNav from "../components/BottomNav";

const Cart = ({ navigation }) => {
  const handleGoBack = () => {
    navigation.goback();
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={{ flexBasis: "90%", marginHorizontal: 10 }}>
        <View
          style={{
            flexBasis: "10%",
            alignItems: "center",
            flexDirection: "row",
            marginHorizontal: 20,
            width: "50%",
            justifyContent: "space-between",
          }}
        >
          <Ionicons name="arrow-back-outline" size={24} color="grey" />
          <View style={{ flexDirection: "column", alignItems: "center" }}>
            <Text style={{ fontWeight: "bold", fontSize: 16 }}>Cart list</Text>
            <Text style={{ color: "grey" }}>(3 Items)</Text>
          </View>
        </View>
        <View
          style={{
            flexBasis: "40%",
          }}
        >
          <View
            style={{
              flexDirection: "row",
              width: "100%",
            }}
          >
            <View
              style={{
                flexBasis: "20%",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <Image
                style={{ width: "90%", height: 50, borderRadius: 10 }}
                source={{
                  uri: "https://i.pinimg.com/564x/24/d6/2f/24d62f4d8ab7d204bf65b86aa24f372f.jpg",
                }}
              />
            </View>
            <View
              style={{
                flexDirection: "row",
                width: "100%",
              }}
            >
              <View style={{ flexBasis: "50%" }}>
                <Text style={{ fontSize: 18, fontWeight: "bold" }}>
                  Pinarello Bike
                </Text>
                <Text style={{ fontSize: 13, color: "#a1a1a1" }}>
                  Mountain Bike
                </Text>
                <View
                  style={{
                    flexDirection: "row",
                  }}
                >
                  <Text style={{ color: "orange" }}>$</Text>
                  <Text style={{ fontWeight: "bold" }}>1,700.00</Text>
                </View>
              </View>
              <View style={{ flex: 1 }}>
                <View
                  style={{
                    flexDirection: "row",
                    width: "100%",
                  }}
                >
                  <MaterialCommunityIcons
                    style={{
                      borderRadius: 50,
                      borderWidth: 1,
                      borderColor: "grey",
                    }}
                    name="delete-outline"
                    size={24}
                    color="orange"
                  />
                </View>
                <View
                  style={{
                    flexDirection: "row",
                    width: "100%",
                  }}
                >
                  <Feather
                    style={styles.iconBg}
                    name="minus"
                    size={24}
                    color="#fff"
                  />
                  <Text>1</Text>
                  <Feather
                    style={styles.iconBg}
                    name="plus"
                    size={24}
                    color="orange"
                  />
                </View>
              </View>
            </View>
            <View>
              <View></View>
              <View></View>
            </View>
          </View>
        </View>
        <View style={{ flexBasis: "30%" }}></View>
        <View style={{ flex: 1, alignItems: "center" }}>
          <TouchableOpacity
            style={{
              backgroundColor: "darkorange",
              paddingHorizontal: 45,
              borderRadius: 15,
              paddingVertical: 20,
            }}
          >
            <Text style={{ color: "#fff", fontSize: 16 }}>
              Proceed to Checkout
            </Text>
          </TouchableOpacity>
        </View>
      </View>
      <BottomNav
        // activeBtn={activeBtn}
        // setActiveBtn={setActiveBtn}
        navigation={navigation}
      />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: statusBarConfig,
  iconBg: {
    backgroundColor: "#313131",
    borderRadius: 50,
  },
});

export default Cart;
